INSERT INTO `a_file` VALUES ('1', 'sited', '/', '0', '1', '', 'ftl', '', '<#assign key=ctx.param(\'key\',\'\') />\n<#assign tag=ctx.paramAsInt(\'tag\',0) />\n<#assign list=eSiteD.findAddins(tag,key) />\n\n<!DOCTYPE html>\n\n<html xmlns=\"http://www.w3.org/1999/xhtml\">\n<head>\n    <title>SiteD 插件收集站</title>\n    <meta http-equiv=\"Content-Security-Policy\" content=\"script-src *.noear.org *.cnzz.com cdn.bootcss.com \'unsafe-inline\' \'unsafe-eval\';\">\n    <meta http-equiv=\"Content-Type\" content=\"text/html; charset=utf-8\"/>\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0, minimum-scale=1.0, maximum-scale=1.0, user-scalable=yes\" />\n    <meta name=\"keywords\" content=\"SiteD,StieD插件,插件中心,插件资源\" />\n    <meta name=\"description\" content=\"为支持[SiteD插件]的app，提供资源扩展~~~\"/>\n    \n    <meta content=\"yes\" name=\"apple-mobile-web-app-capable\">\n    <meta content=\"yes\" name=\"apple-touch-fullscreen\">\n    <meta content=\"black\" name=\"apple-mobile-web-app-status-bar-style\">\n    <meta content=\"telephone=no\" name=\"format-detection\"/>\n        \n    <meta http-equiv=\"Expires\" content=\"0\">\n	<meta http-equiv=\"Pragma\" content=\"no-cache\">\n	<meta http-equiv=\"Cache-control\" content=\"no-cache\">\n	<meta http-equiv=\"Cache\" content=\"no-cache\">\n    \n    <script src=\"${XUtil.cfgGet(\'cdn_zepto1\')}\"></script>\n    <style>\n        * { margin:0px; padding:0px; font-family: \'幼圆\';} \n        \n        img { border: none; } \n        html { background: #fff; }\n        body {min-height:100%;}\n        header a{color: #000;}\n        section a{color: #000;}\n        .block {max-width:640px; min-width: 320px; overflow: hidden; margin: auto; background: #fff;}\n        .button{display: inline-block;line-height: 30px;}\n        .search {text-align:center; margin-bottom:10px;overflow:hidden;}\n            .search input {height:40px; width:50%;border:1px solid #00CCFF;float:left;}\n            .search button { height:42px;widt