INSERT INTO `a_file` VALUES ('15', '_frm', '/.admin/config/edit.ftl', '0', '1', '', 'ftl', '', '<#assign cid=ctx.paramAsInt(\'cid\',0) />\n<#assign cm=db.table(\"a_config\").where(\'cfg_id=?\',cid).select(\"*\").getMap() />\n\n<html class=\"frm20\">\n<head>\n    <title>配置</title>\n    <link href=\"/.admin/.css/main.css\" rel=\"stylesheet\" />\n    <script src=\"${XUtil.cfgGet(\'cdn_jquery2\')}\"></script>\n    <script src=\"${XUtil.cfgGet(\'cdn_layer2\')}\"></script>\n    <script>\n        function file_save() {\n            var vm = {\n                cid:${cid},\n                tag:$(\'#tag\').val(),\n                name:$(\'#name\').val(),\n                value:$(\'#value\').val(),\n                note:$(\'#note\').val()\n            };\n            \n            if(vm.name==\'\'){\n                alert(\'请输入name\');\n                return;\n            }\n            \n            $.ajax({\n                url:\"edit_ajax.jsx\",\n                type:\"POST\",\n                data:vm,\n                success:function(data){\n                    if(data.code==1){\n                        layer.msg(\'操作成功\');\n                        window.setTimeout(function(){\n                            self.location=\'./\';\n                        },1000);\n                    }else{\n                        layer.msg(\'操作失败\');\n                    }\n                }\n            });\n        }\n    </script>\n</head>\n<body>\n\n<h2><a href=\"javascript:history.back()\" >变量列表</a> / 变量</h2>\n<hr/>\n<detail>\n<form>\n    <table>\n        <tr>\n          <td>分组</td><td><input type=\"text\" id=\"tag\" value=\"${cm.tag!}\" />*</td>\n        </tr>\n        <tr>\n          <td>变量</td><td><input type=\"text\" id=\"name\" value=\"${cm.name!}\" />*</td>\n        </tr>\n        <tr>\n          <td>内容</td><td><input type=\"text\" id=\"value\" class=\"longtxt\" value=\"${cm.value!}\"  /></td>\n        </tr>\n        <tr>\n          <td>备注</td><td><input type=\"text\" id=\"note\" class=\"longtxt\" value=\"${cm.note!}\"  /></td>\n        </tr>\n        <tr>\n          <td></td>\n          <td>\n                <button class=\"edit\" type=\"button\" onclick=\"file_save()\">保存</button>\n          </td>\n        </tr>\n    </table>\n    </form>\n<detail>\n</body>\n</html>', null, '2019-07-19 15:31:14');
INSERT INTO `a_file` VALUES ('17', '_frm', '/.admin/.css/main.css', '1', '1', '', 'css', 'text/css;charset=utf-8', '/*\ndurian ui\nver:1.0.21\nauthor:noear\n*/\n\n* { margin: 0px; padding: 0px; font-size: 14px;}\nhtml { background-color: #fff; overflow: hidden; -webkit-overflow-scrolling:touch;}\nhtml.frm{overflow:auto;}\nhtml.frm10{overflow:auto;}\nhtml.frm10 body{margin: 10px;}\nhtml.frm20{overflow:auto;}\nhtml.frm20 body{margin: 20px;}\n\nbody{font-family:\'微软雅黑\',sans-serif;}\nul { list-style: none; display: block; }\nli,p { display: block; }\ni, em { font-style: normal; }\nth { font-weight: normal; }\ntable { border-collapse: collapse; border-spacing: 0px; border: 0px; }\nimg { border: none; }\na {color: #000;}\ninput:focus { outline:none;}\nnote,n{display: inline; color:#999;}\ncode{color: green;}\n.left{text-align: left!important;}\n.right{text-align: right!important;}\n.center{text-align: center!important;}\n.break{word-wrap:break-word; word-break:break-all;}\n\nh0,h0 *{font-size: 24px;display: inline;}\nh1,h1 *{font-size: 18px;}\nh2,h2 *{font-size: 16px;}\nh3,h3 *{font-size: 14px;}\nh4,h4 *{font-size: 12px;}\nh5,h5 *{font-size: 10px;}\nh6,h6 *{font-size: 8px;}\n\nhr{margin-bottom: 10px; margin-top:10px; height:0px;border:none;border-top:1px solid #ccc;}\n\ninput,select,button{vertical-align:middle;}\n\ncell{display: table-cell;}\ncell th{color: #999; }\ncell td{text-align: left; padding-right: 20px;}\n\ninput[type=\"text\"],input[type=\"date\"],input[type=\'datetime-local\'],input[type=\'datetime\'],input[type=\"password\"]{ border: 1px solid #C9C9C9; padding-left: 2px; height: 26px;  line-height: 20px; }\n\ntextarea{ border: 1px solid #C9C9C9; width: 394px; padding: 2px; }\n\na.btn{display: inline-block; text-decoration:none; vertical-align: middle;text-align: center; line-height: 24px; height: 24px!important;}\n\nbutton,a.btn { background-color: #fff; border: 1px solid #189aca; color: #189aca; min-width: 40px; padding: 0 10px; height: 26px; font-size: 11p