INSERT INTO `a_file` VALUES ('30', 'sited', '/dev/plugin/', '0', '1', '', 'ftl', '', '<#assign key=ctx.param(\"key\",\"\") />\n<#assign puid=ctx.cookie(XUtil.cfgGet(\"sited_cookie_puid\")) />\n<#if key=\"\">\n<#assign slist=db.table(\"sites s\")\n                 .leftJoin(\"site_stat t\").on(\"s.title=t.title\")\n                 .where(\'s.is_del=0 AND s.puid=?\',puid)\n                 .select(\"s.guid,s.ver,s.author,s.site_id,s.title,s.is_ok,s.is_app,s.is_del,s.update_time,t.log1_count,t.log2_count,t.log3_count,t.log4_count,t.log5_count,t.look_count,t.like_count\")\n                 .getMapList() />\n<#else>\n<#assign slist=db.table(\"sites s\")\n                 .leftJoin(\"site_stat t\").on(\"s.title=t.title\")\n                 .where(\'s.is_del=0 AND s.puid=? AND s.title LIKE ?\',puid,\'%\'+key+\'%\')\n                 .select(\"s.guid,s.ver,s.author,s.site_id,s.title,s.is_ok,s.is_app,s.is_del,s.update_time,t.log1_count,t.log2_count,t.log3_count,t.log4_count,t.log5_count,t.look_count,t.like_count\")\n                 .getMapList() />\n</#if>\n\n\n<!doctype html>\n<html lang=\"zh_CN\" class=\"frm10\">\n<head>\n	<meta charset=\"UTF-8\">\n	<link href=\"/.admin/.css/main.css\" rel=\"stylesheet\" />\n	<script src=\"${XUtil.cfgGet(\'cdn_zepto1\')}\"></script>\n	<script>\n	function search(){\n	    var key = $(\'#key\').val();\n	    if(key.length>0){\n            window.location = \"./?key=\"+key;\n        }\n        else{\n            window.location = \"./\";\n        }\n	}\n	$(function(){\n		$(\"#key\").keydown(function(e){\n			if(e.keyCode==13){\n		  		search();\n		  	}\n		});\n    });\n	</script>\n</head>\n<body>\n\n<main>\n<toolbar>\n    <left>插件：<input type=\"text\" id=\"key\" value=\"${key}\"/> <button type=\"button\" onclick=\"search()\">查询</button></left>\n    <right>\n        <span class=\"t5\">注：已上架=WEB可见；已过审=APP可见</span>\n    </right>\n</toolbar>\n<datagrid>\n<table>\n  <thead>\n  <tr>\n    <td width=\"50\">ID</td>\n    <td>名称</td>\n    <td>作者</td>\n    <td width=\"60\">安装数</td>\n    <td width=\"60\">更新数</td>\n    <td width=\"60\">打开数</td>\n    <td width=\"60\">出错数</td>\n    <td width=\"60\">收藏数</td>\n    <td width=\"60\">点击数</td>\n    <td width=\"1\"></td>\n    <td width=\"50\">已上架</td>\n    <td width=\"50\">已过审</td>\n    <td width=\"50\">已删除</td>\n    <td width=\"130\"></td>\n  </tr>\n  </thead>\n  <tbody>\n  <#list slist as s1>\n    <tr title=\"更新时间：${s1.update_time?string(\'yyyy-MM-dd HH:mm:ss\')}\">\n        <td>${s1.site_id}</td>\n        <td class=\"left\">${s1.title}.v${s1.ver}</td>\n        <td class=\"left\">${s1.author}</td>\n        <td class=\"right\">${s1.log1_count!}</td>\n        <td class=\"right\">${s1.log2_count!}</td>\n        <td class=\"right\">${s1.log3_count!}</td>\n        <td class=\"right\">${s1.log5_count!}</td>\n        <td class=\"right\">${s1.look_count!}</td>\n        <td class=\"right\">${s1.like_count!}</td>\n        <td></td>\n        <td>${s1.is_ok?string(\'✔\',\'\')}</td>\n        <td>${s1.is_app?string(\'✔\',\'\')}</td>\n        <td>${s1.is_del?string(\'✔\',\'\')}</td>\n        <td>\n            <a class=\"t2\" href=\"set.ftl?sid=${s1.site_id}\">设置</a>\n            |\n            <a class=\"t2\" target=\"_blank\" href=\"view1.sited.xml?guid=${s1.guid}\">源码</a>\n            |\n            <a class=\"t2\" href=\"view2.sited?guid=${s1.guid}\">导出</a>\n        </td>\n    </tr>\n  </#list>\n  </tbody>\n</table>\n</datagrid>\n</main>\n\n</body>\n</html>', null, '2019-07-20 13:09:03');
INSERT INTO `a_file` VALUES ('31', 'sited', '/dev/sets/', '0', '1', '', 'ftl', '', '<#assign puid=ctx.cookie(XUtil.cfgGet(\"sited_cookie_puid\")) />\n<#assign um=db.table(\"bcf_user\").where(\"puid=?\",puid).select(\"*\").getMap()/>\n<html class=\"frm10\">\n<head>\n    <title>设置</title>\n    <link href=\"/.admin/.css/main.css\" rel=\"stylesheet\" />\n    <script src=\"${XUtil.cfgGet(\'cdn_jquery2\')}\"></script>\n    <script src=\"${XUtil.cfgGet(\'cdn_layer2\')}\"></script>\n    <script>\n    function user_save() {\n            var vm = {\n                puid:${puid},\n                user_id:$(\'#user_id\').val(),\n                pass_wd:$(\'#pass_wd\').val(),\n                pw_mail:$(\'#pw_mail\').val()\n            };\n            \n            if(vm.user_id==\'\'){\n                alert(\'请输入user_id\');\n                return;\n            }\n            \n            $.ajax({\n                url:\"ajax.jsx\",\n                type:\"POST\",\n                data:vm,\n                success:function(data){\n                    if(data.code==1){\n                        layer.msg(\'操作成功\');\n                    }else{\n                        layer.msg(\'操作失败\');\n                    }\n                }\n            });\n        }\n    </script>\n</head>\n<body>\n<main>\n    <h2>设置</h2>\n<hr />\n<detail>\n\n    <form>\n    <table>\n    <tr>\n        <td>账号</td><td><input type=\"text\" id=\"user_id\" value=\"${um.user_id!}\" disabled=\"disabled\"/></td>\n    </tr>\n    <tr>\n        <td>密码</td><td><input type=\"text\" id=\"pass_wd\"  /></td>\n    </tr>\n    <tr>\n        <td>邮箱</td><td><input type=\"text\" id=\"pw_mail\" value=\"${um.pw_mail!}\" /></td>\n    </tr>\n    \n    <tr>\n        <td></td><td><button type=\"button\" onclick=\"user_save()\">保存</button></td>\n    </tr>\n    \n    </table>\n    </form>\n</detail>\n</main>\n</body>\n</html>', null, '2019-07-18 23:48:27');
INSERT INTO `a_file` VALUES ('32', 'sited', '/dev/login.ftl', '0', '1', '', 'ftl', '', '<!DOCTYPE HTML>\n<html>\n<head>\n  <title>开放平台</title>\n  <link href=\"/.admin/.css/main.css\" rel=\"stylesheet\" />\n  <script src=\"${XUtil.cfgGet(\'cdn_jquery2\')}\"></script>\n  <script src=\"${XUtil.cfgGet(\'cdn_layer2\')}\"></script>\n  <style type=\"text/css\">\n\n    section{width: 300px; padding: 50px; left: calc(50vw - 200px); top: calc(50vh - 5vh - 150px); position: absolute; background: #ddd;}\n    section table {width: 100%; }\n    section table th{text-align: right;}\n    section table td{padding-top: 5px; padding-bottom: 5px;}\n    section table td img{height: 30px; float: left;}\n    section table input{height: 30px!important; width: 100%;}\n    section table button{color: #000; height: 30px; width: 120px;}\n\n    main > p{margin: 10px;line-height: 30px;}\n  </style>\n  <script type=\"text/javascript\">\n      function checkClick(){\n          $.ajax({\n              url:\"./login/ajax/check.jsx\",\n              data:$(\"section form\").serialize(),\n              success:function(data){\n                  if(data.code==1)\n                      location.href=data.url;\n                  else\n                      alert(data.msg);\n              }\n          });\n          return false;\n      }\n      function checkKey() {\n          if (window.event.keyCode == 13)\n              checkClick();\n      }\n\n  </script>\n</head>\n<body onkeydown=\"checkKey()\">\n\n<main>\n  <p>开放平台（进QQ群：316410970，可申请账号）</p>\n\n  <section>\n    <form method=\"post\">\n      <table>\n        <tr><th width=\"60px;\">账号：</th>\n          <td colspan=\"2\"><input type=\"text\" name=\"userName\" placeholder=\"用户名\"/></td>\n        </tr>\n        <tr><th>密码：</th>\n          <td colspan=\"2\"> <input type=\"password\" name=\"passWord\" placeholder=\"密码\"/></td>\n        </tr>\n        <tr><th>验证码：</th>\n          <td><input type=\"text\" name=\"validationCode\"/></td>\n          <td style=\"width: 60px; padding-left: 10px;\"><img src=\"./login/validation/img.jsx\" onclick=\"reloadimg(this)\"/></td>\n        </tr>\n\n        <tr>\n          <th></th>\n          <td colspan=\"2\">\n            <br />\n            <button type=\"button\" onclick=\"checkClick()\">登录</button>\n          </td>\n        </tr>\n      </table>\n    </form>\n  </section>\n</main>\n\n</body>\n</html>\n<script type=\"text/javascript\">\n  function reloadimg(img) {\n    $(img).attr(\"src\",\"./login/validation/img.jsx?date\"+Date());\n  }\n</script>\n', null, '2019-07-19 10:32:27');
INSERT INTO `a_file` VALUES ('33', 'sited', '/dev/plugin/up.ftl', '0', '1', '', 'ftl', '', '<!DOCTYPE html>\n\n<html class=\"frm10\">\n<head>\n    <title>上传</title>\n    <meta http-equiv=\"Content-Type\" content=\"text/html; charset=utf-8\"/>\n    <link type=\"text/css\" href=\"/.admin/.css/main.css\" rel=\"stylesheet\" />\n    <link type=\"text/css\" rel=\"stylesheet\" href=\"/.admin/.css/uploader.css\" />\n    <style>\n        * { margin:0px; padding:0px; font-family: \'Arial Unicode MS\';} \n        img { border: none; } \n        .block {max-width:640px; min-width: 320px; overflow: hidden; margin: auto; }\n#footer { height: 200px; line-height: 200px; color: #727a86; }\n            #footer p { text-align:center;}\n            #footer a{color: #727a86;margin-right: 10px;}\n    </style>\n    \n    <script type=\"text/javascript\" src=\"${XUtil.cfgGet(\'cdn_jquery2\')}\"></script>\n    <script type=\"text/javascript\" src=\"/.admin/.js/dmuploader.min.js\"></script>\n\n    <script type=\"text/javascript\">\n        $(function () {\n            $(\'#uploader\').dmUploader({\n                url: \'up_ajax.jsx\',\n                dataType: \'json\',\n                maxFileSize:60000,\n                allowedTypes: \'text/xml\',\n                onNewFile: function (id, file) {\n                    $(\"#uploader_hint\").text(\"开始上传文件\");\n                },\n                onUploadSuccess: function (id, data) {\n                    if(data.num>0)\n                        $(\"#uploader_hint\").text(\"上传成功\");\n                    else\n                        $(\"#uploader_hint\").text(\"数据接收失败\");\n\n                    if (data.msg) {\n                        $(\"#uploader_hint\").text(data.msg);\n                    }\n                },\n                onUploadError: function (id, message) {\n                    $(\"#uploader_hint\").text(\'上传出错，请重试或联系管理员\');\n                },\n                onFileTypeError: function (file) {\n                    $(\"#uploader_hint\").text(\"只能上传.sited.xml文件\")\n                },\n                onFileSizeError: function (file) {\n                    $(\"#uploader_hint\").text(\"文件不能超过20KB\");\n                }\n            });\n        });\n    </script>\n  </head>\n  <body>\n<h2>上传</h2>\n<hr/>\n\n<main>\n\n      <div class=\"block\">\n	    <div style=\"text-align: center; padding: 40px 0px 40px 0px;line-height: 40px;\">\n		    熟悉HTML/CSS+XML+JS/jQuery即可开发插件！\n		    <br />\n            欢迎上传SiteD插件\n            <br />\n		    （开发者交流QQ群：316410970）\n		    \n	    </div>\n    </div>\n\n    <div class=\"block\">\n       \n        <!-- 上传区 -->\n        <div id=\"uploader\" class=\"uploader\" style=\"margin:20px;\">\n            <div>把开发好的插件拖放到此处</div>\n            <div class=\"or\">-或-</div>\n            <div class=\"browser\">\n                <label>\n                <span>点击选择文件</span>\n                <input type=\"file\" name=\"file\" multiple=\"multiple\" title=\'点击添加文件\' />\n                </label>\n            </div>\n        </div>\n       <!-- 提示区 -->\n       <div id=\"uploader_hint\" style=\"margin:20px;\"></div>\n    </div>\n</main>      \n  </body>\n</html>\n', null, '2019-07-18 17:56:49');
