INSERT INTO `a_file` VALUES ('39', 'sited', '/.admin/sited/user/', '0', '1', '', 'ftl', '', '<#assign key=ctx.param(\"key\",\"\") />\n\n\n<#if key=\"\">\n<#assign ulist=db.table(\"bcf_user\").select(\"*\").getMapList() />\n<#else>\n<#assign ulist=db.table(\"bcf_user\").where(\'user_id like ?\',\'%\'+key+\'%\').select(\"*\").getMapList() />\n</#if>\n<html class=\"frm10\">\n<head>\n    <title>用户</title>\n    <link href=\"/.admin/.css/main.css\" rel=\"stylesheet\" />\n    <script src=\"${XUtil.cfgGet(\'cdn_zepto1\')}\"></script>\n    <script>\n    function search(){\n	    var key = $(\'#key\').val();\n	    if(key.length>0){\n            window.location = \"./?key=\"+key;\n        }\n        else{\n            window.location = \"./\";\n        }\n	}\n	$(function(){\n		$(\"#key\").keydown(function(e){\n			if(e.keyCode==13){\n		  		search();\n		  	}\n		});\n    });\n    </script>\n</head>\n<body>\n<toolbar>\n    <left>\n        账号：<input id=\"key\" type=\"text\" value=\"${key}\" />\n        <button type=\"button\" onclick=\"search\">查询</button>\n        <a class=\"mar20\"></a><a class=\"btn edit\" href=\"set.ftl?puid=0\">添加</a>\n    </left>\n</toolbar>\n<datagrid>\n    <table><thead>\n        <tr><td width=\"50\">PUID</td><td>账号</td><td width=\"400px\">密码</td><td>邮箱</td><td>备注</td><td width=\"40px\"></td></tr>\n        </thead>\n        <tbody>\n        <#list ulist as u1>\n            <tr>\n                <td>${u1.puid!}</td>\n                <td class=\"left\">${u1.user_id!}</td>\n                <td class=\"left\">${u1.pass_wd!}</td>\n                <td class=\"left\">${u1.pw_mail!}</td>\n                <td class=\"left\">${u1.note!}</td>\n                <td><a class=\"t2\" href=\"./set.ftl?puid=${u1.puid}\">设置</a></td>\n            </tr>\n        </#list>\n        </tbody></table>\n</datagrid>\n</body>\n</html>', null, '2019-07-19 00:25:13');
INSERT INTO `a_file` VALUES ('40', 'sited', '/.admin/sited/plugin/', '0', '1', '', 'ftl', '', '<#assign key=ctx.param(\"key\",\"\") />\n\n<#if key=\"\">\n<#assign slist=db.table(\"sites s\")\n                 .leftJoin(\"site_stat t\").on(\"s.title=t.title\")\n                 .where(\'s.is_del=0\')\n                 .orderBy(\"update_time desc\")\n                 .select(\"s.guid,s.ver,s.author,s.site_id,s.title,s.is_ok,s.is_app,s.is_del,s.is_exa,s.is_vip,s.update_time,t.log1_count,t.log2_count,t.log3_count,t.log4_count,t.log5_count,t.look_count,t.like_count\")\n                 .getMapList() />\n<#else>\n<#assign slist=db.table(\"sites s\")\n                 .leftJoin(\"site_stat t\").on(\"s.title=t.title\")\n                 .where(\'s.is_del=0 AND s.title LIKE ?\',\'%\'+key+\"%\")\n                 .orderBy(\"update_time desc\")\n                 .select(\"s.guid,s.ver,s.author,s.site_id,s.title,s.is_ok,s.is_app,s.is_del,s.is_exa,s.is_vip,s.update_time,t.log1_count,t.log2_count,t.log3_count,t.log4_count,t.log5_count,t.look_count,t.like_count\")\n                 .getMapList() />\n</#if>\n\n<!doctype html>\n<html lang=\"zh_CN\" class=\"frm10\">\n<head>\n	<meta charset=\"UTF-8\">\n	<link href=\"/.admin/.css/main.css\" rel=\"stylesheet\" />\n	<script src=\"${XUtil.cfgGet(\'cdn_zepto1\')}\"></script>\n	<script>\n	function search(){\n	    var key = $(\'#key\').val();\n	    if(key.length>0){\n            window.location = \"./?key=\"+key;\n        }\n        else{\n            window.location = \"./\";\n        }\n	}\n	$(function(){\n		$(\"#key\").keydown(function(e){\n			if(e.keyCode==13){\n		  		search();\n		  	}\n		});\n    });\n	</script>\n</head>\n<body>\n\n<main>\n<toolbar>\n    <left>插件：<input type=\"text\" id=\"key\" value=\"${key}\"/> <button type=\"button\" onclick=\"search()\">查询</button></left>\n    <right>\n        <span class=\"t5\">注：统计数据为30天的累加</span>\n    </right>\n</toolbar>\n<datagrid>\n<table>\n  <thead>\n  <tr>\n    <td width=\"50\">ID</td>\n    <td>名称</td>\n    <td>作者</td>\n    <td width=\"60\">安装数</td>\n    <td width=\"60\">更新数</td>\n    <td width=\"60\">出错数</td>\n    <td width=\"1\"></td>\n    <td width=\"60\">权限</td>\n    <td width=\"60\">已上架</td>\n    <td width=\"60\">已过审</td>\n    <td width=\"60\">已删除</td>\n    <td width=\"100\">更新时间</td>\n    <td width=\"40\"></td>\n  </tr>\n  </thead>\n  <tbody>\n  <#list slist as s1>\n    <tr>\n        <td>${s1.site_id}</td>\n        <td class=\"left\">${s1.title}.v${s1.ver}</td>\n        <td class=\"left\">${s1.author}</td>\n        <td class=\"right\">${s1.log1_count!}</td>\n        <td class=\"right\">${s1.log2_count!}</td>\n        <td class=\"right\">${s1.log5_count!}</td>\n        <td></td>\n        <td>${s1.is_vip}</td>\n        <td>${s1.is_ok?string(\'✔\',\'\')}</td>\n        <td>${s1.is_app?string(\'✔\',\'\')}</td>\n        <td>${s1.is_del?string(\'✔\',\'\')}</td>\n        <td>${s1.update_time?string(\'yyyy-MM-dd\')}</td>\n        <td><a class=\"t2\" href=\"./set.ftl?sid=${s1.site_id}\">设置</a></td>\n    </tr>\n  </#list>\n  </tbody>\n</table>\n</datagrid>\n</main>\n\n</body>\n</html>', null, '2019-07-20 13:12:11');
