INSERT INTO `a_file` VALUES ('42', '_frm', '/.admin/config/edit_ajax.jsx', '0', '1', '', 'javascript', 'application/json', 'var cid = ctx.paramAsInt(\'cid\',0);\nvar name = ctx.param(\'name\',\'\');\n\nif(name !== \'\'){\n    \n    if(cid>0){\n        db.table(\'a_config\')\n        .set(\'`name`\',name)\n        .set(\'`tag`\',ctx.param(\'tag\',\'\'))\n        .set(\'`value`\',ctx.param(\'value\',\'\'))\n        .set(\'`note`\',ctx.param(\'note\',\'\'))\n        .where(\'cfg_id=?\',cid)\n        .update();\n    }else{\n        db.table(\'a_config\')\n        .set(\'`name`\',name)\n        .set(\'`tag`\',ctx.param(\'tag\',\'\'))\n        .set(\'`value`\',ctx.param(\'value\',\'\'))\n        .set(\'`note`\',ctx.param(\'note\',\'\'))\n        .insert();\n    }\n    \n    return {code:1,msg:\'\'};\n    \n}else{\n    return {code:0,msg:\'\'};\n}\n', null, '2019-07-19 15:30:44');
