INSERT INTO `a_file` VALUES ('53', '_frm', '/test.htm', '0', '1', '', 'html', '', '<html>\n<head>\n    <title>test</title>\n    <meta http-equiv=\"Content-Type\" content=\"text/html; charset=UTF-8 \" />\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0, minimum-scale=1.0, maximum-scale=1.0, user-scalable=yes\" />\n    <script>\n    \n        \n        var fm = new (function(){\n             this.a = function(){\n                return 1;  \n            }\n            \n            this.b = function(){\n                return 2;\n            }\n        })();\n        \n        alert(fm.a() + fm.b());\n    </script>\n</head>\n<body>\n    <a href=\"itms-services://?action=download-manifest&url=https://www.coohou.com/app/kuhou/manifest.plist\">安装...（先删掉已安装的app）</a>\n</body>\n</html>', null, '2019-07-19 21:33:06');
INSERT INTO `a_file` VALUES ('54', '_frm', '/.admin/file/list.ftl', '0', '1', '', 'ftl', '', '<#assign tag=ctx.param(\'tag\',\'\') />\n<#assign key=ctx.param(\'key\',\'\') />\n\n<#if key=\'\'>\n<#assign flist=db.table(\"a_file\").where(\'tag=?\',tag)\n                 .orderBy(\'path asc\')\n                 .select(\"file_id,path,is_staticize,is_editable,link_to,edit_mode,content_type,update_fulltime\")\n                 .getMapList() />\n<#else>\n<#assign flist=db.table(\"a_file\").where(\'tag=?\',tag).and(\'path like ?\',\'%\'+key+\'%\')\n                 .orderBy(\'path asc\')\n                 .select(\"file_id,path,is_staticize,is_editable,link_to,edit_mode,content_type,update_fulltime\")\n                 .getMapList() />\n</#if>\n\n<html class=\"frm10\">\n<head>\n    <title>文件</title>\n    <link href=\"/.admin/.css/main.css\" rel=\"stylesheet\" />\n    <script src=\"${XUtil.cfgGet(\'cdn_zepto1\')}\"></script>\n    <script>\n    function file_set(id){\n        if(!id){\n            id = 0;\n        }\n    \n        location.href=(\'set.ftl?fid=\'+id);\n    }\n    \n    function search(){\n	    var key = $(\'#key\').val();\n	    if(key.length>0){\n            window.location = \"./list.ftl?tag=${tag}&key=\"+key;\n        }\n        else{\n            window.location = \"./list.ftl?tag=${tag}\";\n        }\n	}\n	\n	$(function(){\n		$(\"#key\").keydown(function(e){\n			if(e.keyCode==13){\n		  		search();\n		  	}\n		});\n    });\n    </script>\n</head>\n<body>\n<main>\n<toolbar>\n    <left>\n        路径：<input id=\"key\" type=\"text\" value=\"${key}\" />\n        <button type=\"button\" onclick=\"search()\">查询</button>\n        <a class=\"mar20\"></a><a class=\"btn edit\" href=\"set.ftl?fid=0\">添加文件</a>\n    </left>\n    <right>\n        <a class=\"btn\" href=\"../code/\" target=\"_blank\">源码编辑</a>\n    </right>\n</toolbar>\n<datagrid>\n    <table><thead>\n        <tr>\n            <td>路径</td>\n            <td>跳转到</td>\n            <td width=\"200\">内容类型</td>\n            <td width=\"80\">编辑模式</td>\n            <td width=\"50\">静态化</td>\n            <td width=\"50\">可编辑</td>\n            <td width=\"80\"></td>\n        </tr>\n        </thead>\n        <tbody>\n        <#list flist as f1>\n            <tr><td class=\"left\">${f1.path!}</td>\n                <td  class=\"left\">${f1.link_to!}</td>\n                <td  class=\"left\">${f1.content_type!}</td>\n                <td  class=\"left\">${f1.edit_mode!}</td>\n                <td>${f1.is_staticize?string(\'静\',\'\')}</td>\n                <td>${f1.is_editable?string(\'编\',\'\')}</td>\n                <td>\n                <a class=\"t2\" href=\'set.ftl?fid=${f1.file_id}\'>编辑</a>\n                |\n                <a class=\"t2\" href=\'../code/edit.ftl?fid=${f1.file_id}&_p=${f1.path}\' target=\"_blank\">源码</a>\n                </td>\n            </tr>\n        </#list>\n        </tbody></table>\n</datagrid>\n</main>\n</body>\n</html>', null, '2019-07-19 00:24:34');
INSERT INTO `a_file` VALUES ('55', 'sited', '/.admin/sited/plugin/set.ftl', '0', '1', '', 'ftl', '', '<#assign sid=ctx.paramAsInt(\'sid\',0) />\n<#assign sm=db.table(\"sites\").where(\'site_id=?\',sid).select(\"*\").getMap() />\n\n<html class=\"frm20\">\n<head>\n    <title>插件</title>\n    <link href=\"/.admin/.css/main.css\" rel=\"stylesheet\" />\n    <script src=\"${XUtil.cfgGet(\'cdn_jquery2\')}\"></script>\n    <script src=\"${XUtil.cfgGet(\'cdn_layer2\')}\"></script>\n    <script>\n        function file_save() {\n            var vm = {\n                sid:${sid},\n                is_vip:$(\'#is_vip\').val(),\n                is_exa:$(\'#is_exa\').is(\':checked\')?1:0,\n                is_app:$(\'#is_app\').is(\':checked\')?1:0,\n                is_ok:$(\'#is_ok\').is(\':checked\')?1:0,\n                is_del:$(\'#is_del\').is(\':checked\')?1:0\n            };\n            \n            $.ajax({\n                url:\"set_ajax.jsx\",\n                type:\"POST\",\n                data:vm,\n                success:function(data){\n                    if(data.code==1){\n                        layer.msg(\'操作成功\');\n                        window.setTimeout(function(){\n                            self.location=\'./\';\n                        },1000);\n                    }else{\n                        layer.msg(\'操作失败\');\n                    }\n                }\n            });\n        }\n    </script>\n</head>\n<body>\n\n<h2><a href=\"javascript:history.back()\" >插件列表</a> / 插件</h2>\n<hr/>\n<detail>\n<form>\n    <table>\n        <tr>\n          <td>名称</td><td><input type=\"text\" id=\"title\" value=\"${sm.title!}\" disabled=\'disabled\' /></td>\n        </tr>\n        <tr>\n          <td>作者</td><td><input type=\"text\" id=\"author\" value=\"${sm.author!}\" disabled=\'disabled\'/></td>\n        </tr>\n        <tr>\n          <td>路径</td><td><input type=\"text\" class=\"longtxt\" id=\"file\" value=\"${sm.file!}\" disabled=\'disabled\'/></td>\n        </tr>\n        <tr>\n          <td>guid</td><td><input type=\"text\" id=\"guid\" value=\"${sm.guid!}\" disabled=\'disabled\'/></td>\n        </tr>\n        <tr>\n          <td>puid</td><td><input type=\"text\" id=\"puid\" value=\"${sm.puid!}\" disabled=\'disabled\'/></td>\n        </tr>\n        <tr>\n          <td>权限</td><td><input type=\"text\" id=\"is_vip\" value=\"${sm.is_vip!}\" />（0：所有人可用；1：vip可用；2：svip可用）</td>\n        </tr>\n        <tr>\n          <td>状态</td>\n          <td>\n            <checkbox>\n                <label class=\"mar10-r\"><input type=\"checkbox\" id=\"is_app\" ${(sm.is_app?string(\'checked\',\'\'))!} /><a>过审（APP可见）</a></label>\n            </checkbox>\n          </td>\n        </tr>\n        <tr>\n          <td></td>\n          <td>\n            <checkbox>\n                <label class=\"mar10-r\"><input type=\"checkbox\" id=\"is_ok\" ${(sm.is_ok?string(\'checked\',\'\'))!} /><a>上架（WEB可见）</a></label>\n            </checkbox>\n          </td>\n        </tr>\n        <tr>\n          <td></td>\n          <td>\n            <checkbox>\n                <label class=\"mar10-r\"><input type=\"checkbox\" id=\"is_del\" ${(sm.is_del?string(\'checked\',\'\'))!} /><a>删除</a></label>\n            </checkbox>\n          </td>\n        </tr>\n        <tr>\n          <td></td>\n          <td>\n            <checkbox>\n                <label class=\"mar10-r\"><input type=\"checkbox\" id=\"is_exa\" ${(sm.is_exa?string(\'checked\',\'\'))!} /><a>审核可见</a></label>\n            </checkbox>\n          </td>\n        </tr>\n        <tr>\n          <td></td>\n          <td>\n                <button class=\"edit\" type=\"button\" onclick=\"file_save()\">保存</button>\n          </td>\n        </tr>\n    </table>\n    </form>\n<detail>\n</body>\n</html>', null, '2019-07-19 00:06:47');
INSERT INTO `a_file` VALUES ('56', 'sited', '/.admin/sited/user/set.ftl', '0', '1', '', 'ftl', '', '<#assign puid=ctx.paramAsInt(\'puid\',0) />\n<#assign um=db.table(\"bcf_user\").where(\'puid=?\',puid).select(\"*\").getMap() />\n\n<html class=\"frm20\">\n<head>\n    <title>插件</title>\n    <link href=\"/.admin/.css/main.css\" rel=\"stylesheet\" />\n    <script src=\"${XUtil.cfgGet(\'cdn_jquery2\')}\"></script>\n    <script src=\"${XUtil.cfgGet(\'cdn_layer2\')}\"></script>\n    <script>\n        function file_save() {\n            var vm = {\n                puid:${puid},\n                user_id:$(\'#user_id\').val(),\n                pass_wd:$(\'#pass_wd\').val(),\n                pw_mail:$(\'#pw_mail\').val(),\n                note:$(\'#note\').val(),\n                is_disabled:$(\'#is_disabled\').is(\':checked\')?1:0\n            };\n            \n            if(vm.user_id==\'\'){\n                alert(\'请输入user_id\');\n                return;\n            }\n            \n            $.ajax({\n                url:\"set_ajax.jsx\",\n                type:\"POST\",\n                data:vm,\n                success:function(data){\n                    if(data.code==1){\n                        layer.msg(\'操作成功\');\n                        window.setTimeout(function(){\n                            self.location=\'./\';\n                        },1000);\n                    }else{\n                        layer.msg(\'操作失败\');\n                    }\n                }\n            });\n        }\n    </script>\n</head>\n<body>\n\n<h2><a href=\"javascript:history.back()\" >用户列表</a> / 用户</h2>\n<hr/>\n<detail>\n<form>\n    <table>\n        <tr>\n          <td>账号</td><td><input type=\"text\" id=\"user_id\" value=\"${um.user_id!}\" />*</td>\n        </tr>\n        <tr>\n          <td>密码</td><td><input type=\"text\" id=\"pass_wd\"  /></td>\n        </tr>\n        <tr>\n          <td>邮箱</td><td><input type=\"text\" id=\"pw_mail\" value=\"${um.pw_mail!}\" /></td>\n        </tr>\n        <tr>\n          <td>备注</td><td><input type=\"text\" id=\"note\" value=\"${um.note!}\" /></td>\n        </tr>\n        \n        <tr>\n          <td></td>\n          <td>\n            <checkbox>\n                <label class=\"mar10-r\"><input type=\"checkbox\" id=\"is_disabled\" ${(um.is_disabled?string(\'checked\',\'\'))!} /><a>禁用</a></label>\n            </checkbox>\n          </td>\n        </tr>\n        <tr>\n          <td></td>\n          <td>\n                <button class=\"edit\" type=\"button\" onclick=\"file_save()\">保存</button>\n          </td>\n        </tr>\n    </table>\n    </form>\n<detail>\n</body>\n</html>', null, '2019-07-18 23:39:17');
INSERT INTO `a_file` VALUES ('57', 'sited', '/.admin/sited/user/set_ajax.jsx', '0', '1', '', 'javascript', 'application/json', 'var puid = ctx.paramAsInt(\'puid\',0);\nvar user_id = ctx.param(\'user_id\',\'\');\nvar pass_wd = ctx.param(\'pass_wd\',\'\');\nif(user_id !== \'\'){\n    var qr = db.table(\'bcf_user\').set(\'user_id\',user_id);\n      \n    if(pass_wd !==\'\'){\n        pass_wd = XUtil.sha1(user_id+\'#\'+pass_wd).toUpperCase();\n        qr.set(\'pass_wd\',pass_wd);\n    }\n    \n    qr.set(\'pw_mail\',ctx.param(\'pw_mail\',\'\'));\n    qr.set(\'is_disabled\',ctx.paramAsInt(\'is_disabled\',0));\n    qr.set(\'note\',ctx.param(\'note\',\'\'));\n    \n    if(puid>0){\n        qr.where(\'puid=?\',puid).update();\n    }else{\n        qr.insert();\n    } \n    \n    return {code:1,msg:\'\'};\n}else{\n    return {code:0,msg:\'\'};\n}', null, '2019-07-18 23:40:25');
INSERT INTO `a_file` VALUES ('58', 'sited', '/.admin/sited/plugin/set_ajax.jsx', '0', '1', '', 'javascript', 'application/json', 'var sid = ctx.paramAsInt(\'sid\',0);\nif(sid>0){\n    db.table(\'sites\')\n      .set(\'is_vip\',ctx.paramAsInt(\'is_vip\',0))\n      .set(\'is_ok\',ctx.paramAsInt(\'is_ok\',0))\n      .set(\'is_app\',ctx.paramAsInt(\'is_app\',0))\n      .set(\'is_del\',ctx.paramAsInt(\'is_del\',0))\n      .set(\'is_exa\',ctx.paramAsInt(\'is_exa\',0))\n      .where(\'site_id=?\',sid)\n      .update();\n    \n    return {code:1,msg:\'\'};\n}else{\n    return {code:0,msg:\'\'};\n}', null, '2019-07-19 00:17:58');
INSERT INTO `a_file` VALUES ('59', 'sited', '/dev/plugin/set.ftl', '0', '1', '', 'ftl', '', '<#assign sid=ctx.paramAsInt(\'sid\',0) />\n<#assign sm=db.table(\"sites\").where(\'site_id=?\',sid).select(\"*\").getMap() />\n\n<html class=\"frm20\">\n<head>\n    <title>插件</title>\n    <link href=\"/.admin/.css/main.css\" rel=\"stylesheet\" />\n    <script src=\"${XUtil.cfgGet(\'cdn_jquery2\')}\"></script>\n    <script src=\"${XUtil.cfgGet(\'cdn_layer2\')}\"></script>\n    <script>\n        function file_save() {\n            var vm = {\n                sid:${sid},\n                is_ok:$(\'#is_ok\').is(\':checked\')?1:0,\n                is_del:$(\'#is_del\').is(\':checked\')?1:0\n            };\n            \n            $.ajax({\n                url:\"set_ajax.jsx\",\n                type:\"POST\",\n                data:vm,\n                success:function(data){\n                    if(data.code==1){\n                        layer.msg(\'操作成功\');\n                        window.setTimeout(function(){\n                            self.location=\'./\';\n                        },1000);\n                    }else{\n                        layer.msg(\'操作失败\');\n                    }\n                }\n            });\n        }\n    </script>\n</head>\n<body>\n\n<h2><a href=\"javascript:history.back()\" >插件列表</a> / 插件</h2>\n<hr/>\n<detail>\n<form>\n    <table>\n        <tr>\n          <td>名称</td><td><input type=\"text\" id=\"title\" value=\"${sm.title!}\" disabled=\'disabled\' /></td>\n        </tr>\n        <tr>\n          <td>作者</td><td><input type=\"text\" id=\"author\" value=\"${sm.author!}\" disabled=\'disabled\'/></td>\n        </tr>\n        <tr>\n          <td>文件</td><td><input type=\"text\" class=\"longtxt\" id=\"file\" value=\"${sm.file!}\" disabled=\'disabled\'/></td>\n        </tr>\n        <tr>\n          <td></td>\n          <td>\n            <checkbox>\n                <label class=\"mar10-r\"><input type=\"checkbox\" id=\"is_ok\" ${(sm.is_ok?string(\'checked\',\'\'))!} /><a>上架（WEB可见）</a></label>\n            </checkbox>\n          </td>\n        </tr>\n        <tr>\n          <td></td>\n          <td>\n            <checkbox>\n                <label class=\"mar10-r\"><input type=\"checkbox\" id=\"is_del\" ${(sm.is_del?string(\'checked\',\'\'))!} /><a>删除</a></label>\n            </checkbox>\n          </td>\n        </tr>\n        <tr>\n          <td></td>\n          <td>\n                <button class=\"edit\" type=\"button\" onclick=\"file_save()\">保存</button>\n          </td>\n        </tr>\n    </table>\n    </form>\n<detail>\n</body>\n</html>', null, '2019-07-18 23:16:35');
INSERT INTO `a_file` VALUES ('60', 'sited', '/dev/plugin/set_ajax.jsx', '0', '1', '', 'javascript', 'application/json', 'var sid = ctx.paramAsInt(\'sid\',0);\nif(sid){\n    db.table(\'sites\')\n      .set(\'is_ok\',ctx.paramAsInt(\'is_ok\',0))\n      .set(\'is_del\',ctx.paramAsInt(\'is_del\',0))\n      .where(\'site_id=?\',sid)\n      .update();\n    \n    return {code:1,msg:\'\'};\n}else{\n    return {code:0,msg:\'\'};\n}', null, '2019-07-18 23:03:49');
INSERT INTO `a_file` VALUES ('61', 'sited', '/dev/sets/ajax.jsx', '0', '1', '', 'javascript', 'application/json', 'var puid = ctx.paramAsInt(\'puid\',0);\nvar user_id = ctx.param(\'user_id\',\'\');\nvar pass_wd = ctx.param(\'pass_wd\',\'\');\nif(user_id !== \'\' && puid>0){\n    var qr = db.table(\'bcf_user\').set(\'user_id\',user_id);\n      \n    if(pass_wd !==\'\'){\n        pass_wd = XUtil.sha1(user_id+\'#\'+pass_wd).toUpperCase();\n        qr.set(\'pass_wd\',pass_wd);\n    }\n    \n    qr.set(\'pw_mail\',ctx.param(\'pw_mail\',\'\'));\n    \n    qr.where(\'puid=?\',puid).update();\n    \n    return {code:1,msg:\'\'};\n}else{\n    return {code:0,msg:\'\'};\n}\n', null, '2019-07-19 17:54:45');
INSERT INTO `a_file` VALUES ('62', 'sited', '/dev/login/ajax/check.jsx', '0', '1', '', 'javascript', 'application/json', 'var userName = ctx.param(\"userName\",\'\');\nvar passWord = ctx.param(\"passWord\",\'\');\nvar vcode = ctx.param(\"validationCode\",\'\').toUpperCase();\n\nvar vkey = XUtil.cfgGet(\'_frm_session_validation\');\nvar vcode2 = ctx.session(vkey).toUpperCase();\n\nif(vcode != vcode2){\n    return {code:0,msg:\'提示：验证码错误！\',v1:vcode,v2:vcode2};\n}\n\nif(!userName || !passWord){\n    return {code:0,msg:\'提示：请输入账号和密码！\'};\n}\n\nvar pwd2 = XUtil.sha1(userName+\"#\"+passWord).toUpperCase();\n\nvar puid = db.table(\"bcf_user\")\n             .where(\"user_id=? AND pass_wd=?\", userName,pwd2)\n             .select(\"puid\")\n             .getValue(0);\n\nif(puid>0){\n    var cookie_mark  = XUtil.cfgGet(\"sited_cookie_mark\");\n    var cookie_puid  = XUtil.cfgGet(\"sited_cookie_puid\");\n    var cookie_token = XUtil.cfgGet(\"sited_cookie_token\");\n    \n    var token = XUtil.md5(puid + \"#\" + cookie_mark).toUpperCase();\n    ctx.cookieSet(cookie_puid, puid + \"\", -1);\n    ctx.cookieSet(cookie_token, token, -1);\n    \n    return {code:1,msg:\"ok\",url:\"/dev/home.ftl\"};\n}else{\n    return {code:0,msg:\"提示：账号或密码不对！\"};\n}', null, '2019-07-19 12:06:35');
INSERT INTO `a_file` VALUES ('63', 'sited', '/dev/login/validation/img.jsx', '0', '1', '', 'javascript', 'image/jpeg', 'var code = XUtil.codeByRandom(4);\nvar vkey = XUtil.cfgGet(\'_frm_session_validation\');\n\nctx.sessionSet(vkey,code);\n\nvar vimg = XUtil.codeToImage(code);\n\nctx.headerSet(\"Pragma\", \"no-cache\");\nctx.headerSet(\"Cache-Control\", \"no-cache\");\nctx.headerSet(\"Expires\", \"0\");\n\nvar ImageIO = Java.type(\'javax.imageio.ImageIO\');\n\nImageIO.setUseCache(false);\nImageIO.write(vimg, \"jpeg\", ctx.outputStream());\n\n', null, '2019-07-19 10:22:02');
INSERT INTO `a_file` VALUES ('64', '_frm', '/.admin/_.jsx', '0', '1', '', 'javascript', '', 'var path = ctx.path();\nif(path.endsWith(\".js\") || path.endsWith(\".css\")) {\n    return;\n}\n\nvar frm_cookie_token = XUtil.cfgGet(\"_frm_cookie_token\");\nvar frm_admin_user   = XUtil.cfgGet(\"_frm_admin_user\");\nvar frm_admin_pwd    = XUtil.cfgGet(\"_frm_admin_pwd\");\n\nvar token = ctx.param(frm_cookie_token);\nif (!token) {\n    token = ctx.cookie(frm_cookie_token);\n} else {\n    ctx.cookieSet(frm_cookie_token, token, -1);\n}\n\nif (!token) {\n    ctx.setHandled(true);\n    ctx.output(\"Please enter the token!\");\n    return;\n}\n\n\nvar token2 = XUtil.sha1(frm_admin_user + \"#\" + frm_admin_pwd).toUpperCase();\n\nif (token !== token2) {\n    ctx.setHandled(true);\n    ctx.output(\"Token is wrong!\");\n    return;\n}', null, '2019-07-19 11:39:22');
INSERT INTO `a_file` VALUES ('65', 'sited', '/dev/_.jsx', '0', '1', '', 'javascript', '', 'var path = ctx.path();\n\nif(path.indexOf(\"/login\")>=0){\n    return;\n}\n\nvar cookie_token = XUtil.cfgGet(\"sited_cookie_token\");\nvar cookie_puid  = XUtil.cfgGet(\"sited_cookie_puid\");\nvar cookie_mark  = XUtil.cfgGet(\"sited_cookie_mark\");\n\nvar token = ctx.cookie(cookie_token);\nvar puid  = ctx.cookie(cookie_puid);\n\nif (!token || !puid) {\n    ctx.setHandled(true);\n    ctx.redirect(\"/dev/login.ftl\");\n    return;\n}\n\nvar token2 = XUtil.md5(puid + \"#\" + cookie_mark).toUpperCase();\n\nif (token !== token2) {\n    ctx.setHandled(true);\n    ctx.redirect(\"/dev/login.ftl\");\n    return;\n}', null, '2019-07-19 12:06:16');
INSERT INTO `a_file` VALUES ('66', '_frm', '/.admin/image/set.ftl', '0', '1', '', 'ftl', '', '<#assign fid=ctx.paramAsInt(\'fid\',0) />\n<#assign fm=db.table(\"a_image\").where(\'img_id=?\',fid).select(\"img_id,tag,content_type,path\").getMap() />\n\n<html class=\"frm20\">\n<head>\n    <title>资源</title>\n    <link href=\"/.admin/.css/main.css\" rel=\"stylesheet\" />\n    <script src=\"${XUtil.cfgGet(\'cdn_jquery2\')}\"></script>\n    <script src=\"${XUtil.cfgGet(\'cdn_layer2\')}\"></script>\n    <script>\n        function file_save() {\n            var vm = {\n                fid:${fid},\n                content_type:$(\'#content_type\').val(),\n                tag:$(\'#tag\').val(),\n                path:$(\'#path\').val()\n            };\n            \n            if(vm.tag==\'\'){\n                alert(\'请输入tag\');\n                return;\n            }\n            \n            if(vm.content_type==\'\'){\n                alert(\'请输入content_type\');\n                return;\n            }\n            \n            if(vm.path==\'\'){\n                alert(\'请输入path\');\n                return;\n            }\n            \n            $.ajax({\n                url:\"set_ajax.jsx\",\n                type:\"POST\",\n                data:vm,\n                success:function(data){\n                    if(data.code==1){\n                        layer.msg(\'操作成功\');\n                        window.setTimeout(function(){\n                            self.location=\'./\';\n                        },1000);\n                    }else{\n                        layer.msg(\'操作失败\');\n                    }\n                }\n            });\n        }\n    </script>\n</head>\n<body>\n\n<datalist id=\"content_type_list\">\n  <option value=\"text/css\">\n  <option value=\"text/xml\">\n  <option value=\"text/html\">\n  <option value=\"text/plain\">\n  <option value=\"image/jpeg\">\n  <option value=\"image/png\">\n  <option value=\"image/gif\">\n  <option value=\"application/x-javascript\">\n  <option value=\"application/octet-stream\">\n  <option value=\"application/json\">\n</datalist>\n\n<h2><a href=\"javascript:history.back()\" >资源列表</a> / 资源</h2>\n<hr/>\n<detail>\n<form>\n    <table>\n        <tr>\n          <td>分组</td><td><input type=\"text\" id=\"tag\" value=\"${fm.tag!}\" />*</td>\n        </tr>\n        <tr>\n          <td>内容类型</td><td><input type=\"text\" id=\"content_type\" list=\"content_type_list\" value=\"${fm.content_type!}\" />*</td>\n        </tr>\n        <tr>\n          <td>路径</td><td><input type=\"text\" id=\"path\" class=\"longtxt\" value=\"${fm.path!}\"  />*</td>\n        </tr>\n        <tr>\n          <td></td>\n          <td>\n                <button class=\"edit\" type=\"button\" onclick=\"file_save()\">保存</button>\n          </td>\n        </tr>\n    </table>\n    </form>\n<detail>\n</body>\n</html>', null, '2019-07-19 21:05:49');
INSERT INTO `a_file` VALUES ('67', '_frm', '/.admin/image/set_ajax.jsx', '0', '1', '', 'javascript', 'application/json', 'var fid = ctx.paramAsInt(\'fid\',0);\nvar content_type = ctx.param(\'content_type\',\'\');\nvar tag = ctx.param(\'tag\',\'\');\nvar path = ctx.param(\'path\',\'\');\n\nif(fid>0){\n    \n    if(content_type && tag && path){\n        db.table(\'a_image\')\n        .set(\'`content_type`\',content_type)\n        .set(\'`tag`\',ctx.param(\'tag\',\'\'))\n        .set(\'`path`\',ctx.param(\'path\',\'\'))\n        .where(\'img_id=?\',fid)\n        .update();\n        \n        return {code:1,msg:\'\'};\n    }\n}\n    \nreturn {code:0,msg:\'\'};\n\n', null, '2019-07-19 21:01:58');
