INSERT INTO `a_file` VALUES ('70', '_frm', '/ntime.jsx', '0', '1', '', 'javascript', 'application/json', 'var add = ctx.param(\'add\');\nvar time = Datetime.Now();\n\nif(add && add.indexOf(\',\')>0){\n  var ss = add.split(\',\');\n  if(ss[1]==\'s\'){\n	time.addSecond(parseInt(ss[0]));\n  }\n  \n  if(ss[1]==\'m\'){\n	time.addMinute(parseInt(ss[0]));\n  }\n  \n  if(ss[1]==\'d\'){\n	time.addDay(parseInt(ss[0]));\n  }\n}\n\nvar ntime = parseInt(((time.getTicks()/1000.0)-1500000000)/10);\n\nreturn time.toString(\'yyyy-MM-dd HH:mm:ss\') +\' = \'+ntime;', null, '2019-07-20 07:22:15');
INSERT INTO `a_file` VALUES ('71', '_frm', '/.admin/help/', '0', '1', '', 'ftl', '', '<html class=\"frm10\">\n<head>\n    <title>帮助</title>\n    <link href=\"/.admin/.css/main.css\" rel=\"stylesheet\" />\n    <script src=\"${XUtil.cfgGet(\'cdn_zepto1\')}\"></script>\n    <script>\n    \n    </script>\n</head>\n<body>\n<main>\n\n<block>\n    <div><h2>JsxEngine</h2></div>\n    <hr/>\n    <ul>\n        <li>\n            <div class=\"t3\">/** 加载函数库 */</div>\n            <div>require(path)</div>\n        </li>\n        <li>\n            <div class=\"t3\">/** 模块和视图 */</div>\n            <div>modelAndView(tml,mod)</div>\n        </li>\n    </ul>\n</block>\n\n<#list XUtil.shared() as l1>\n<block>\n    <div><h2>${l1.name}::${l1.type}</h2></div>\n    <hr/>\n    <ul>\n        <#list l1.methods as s1>\n        <li>\n            <div class=\"t3\">${s1.note!}</div>\n            <div>${s1.code}</div>\n        </li>\n        </#list>\n    </ul>\n</block>\n</#list>\n\n\n</main>\n</body>\n</html>', null, '2019-07-22 17:18:59');

